#CLICK FOR EXAMPLE LOGIC
$clickforexample.add_click({
    $firstoctecttext = $firstoctect.Text
    $secondoctecttext = $secondoctect.Text
    $thirdoctecttext = $thirdoctect.Text
    $fourthoctecttext = $fourthoctect.Text
    
    #GETTING VALUE FOR EXAMPLE CODE
    $textforexamplecode = "chmod $firstoctecttext$secondoctecttext$thirdoctecttext$fourthoctecttext <path-to-file>"

    #PUTTING VALUE INTO EXAMPLE CODE TEXT
    $examplecode.Text = $textforexamplecode

    #ALSO PUTTING VALUE IN HISTORY TEXTBOX IF TEXT LENGTH IS LESS THAN 425
    if ($historytextbox.Text.Length -ge 425){
        $historytextbox.Text = ""
        $historytextbox.AppendText($textforexamplecode)
    }else{
        $historytextbox.AppendText($textforexamplecode)
    }
})

